import { Component, OnInit, ElementRef, OnDestroy } from '@angular/core';
import { } from 'googlemaps';
import { Router } from '@angular/router';
@Component({
  selector: 'evo-landing',
  templateUrl: 'landing.component.html',
  styleUrls: ['landing.component.scss']
})

export class LandingComponent implements OnInit, OnDestroy {
  loggedUser: any;
  searchModel: any = {};
  helperModel: any = {
    label: '',
    latitude: 0,
    longitude: 0,
    zoom: 4
  };
  searchOptions: any = [
    {
      value: 'location',
      label: 'Визначити моє місцезнаходження',
      image: 'data:image/svg+xml;utf8;base64,'
    },
    {
      value: 'address',
      label: 'Вказати адресу',
      image: 'data:image/svg+xml;utf8;base64,'
    },
    {
      value: 'city',
      label: 'Вказати насеений пункт',
      image: 'data:image/svg+xml;utf8;base64,'
    },
    {
      value: 'region',
      label: 'Вказати область',
      image: 'data:image/svg+xml;utf8;base64,'
    },
    {
      value: 'coords',
      label: 'Вказати координати',
      image: 'data:image/svg+xml;utf8;base64,'
    }
  ]
  constructor(private router: Router,
    private _elementRef: ElementRef) {
  }
  setSearchType(type: any) {
    switch (type) {
      case 'location':
        this.getCurrentPosition();
        break;

      default:
        break;
    }
  }

  getCurrentPosition() {
    navigator.geolocation.getCurrentPosition((position) => {
      this.helperModel = {
        latitude: position.coords.latitude,
        longitude: position.coords.longitude,
        zoom: 16
      };
    }, (err) => {
      console.log(err);
    }, { enableHighAccuracy: true });
  }
  ngOnInit() {
    let element = this._elementRef.nativeElement.querySelector('body');
    console.log(element);
  }
  ngOnDestroy(){
    let element = document.getElementsByClassName('pac-container')[0];
    if (element) element.remove();
    console.log(element);
  }
  getAddress(event: any, formControl: any) {
    console.log(event);
    this.helperModel.longitude = event.geometry.location.lng();
    this.helperModel.latitude = event.geometry.location.lat();
    this.helperModel.label = event.formatted_address;
    this.router.navigate(['trucks'], { queryParams: { lat: this.helperModel.latitude, lng: this.helperModel.longitude, label: this.helperModel.label } });
  }
}
